mapper.select:
select t.id, t.cmname, t.cmdirection, (select count(*) from communitymember A WHERE A.communityid = t.id) AS total from community t
cmname LIKE CONCAT('%',#{condition},'%')
order by id DESC limit #{offset},#{limit}

