mapper.select:
SELECT m.id,m.title,m.contents,m.senddata,m.status,m.sendee,m.createuser
FROM  mail m
WHERE m.sendee=#{sendee};

SELECT m.id,m.title,m.contents,m.senddata,m.status,m.sendee,m.createuser
FROM mail m
WHERE  m.id = #{id}

mapper.update:
UPDATE  mail m
SET  m.status = "0"
WHERE  m.id = #{id}

mapper.select:
SELECT m.id,m.title,m.contents,m.senddata,m.status,m.sendee,m.createuser
FROM  mail m
WHERE m.createuser=#{createuser};
